import React, { useEffect, Suspense, lazy } from 'react'
import styled from 'styled-components'
import { BrowserRouter as Router, Redirect, Route, Switch } from 'react-router-dom'
import { useWallet } from '@binance-chain/bsc-use-wallet'
import { ResetCSS } from '@pancakeswap-libs/uikit'
import BigNumber from 'bignumber.js'
import { useFetchPriceList, useFetchProfile, useFetchPublicData } from 'state/hooks'
import GlobalStyle from './style/Global'
import Menu from './components/Menu'
import PageLoader from './components/PageLoader'
// eslint-disable-next-line import/no-duplicates
// Route-based code splitting
// Only pool is included in the main bundle because of it's the most visited page'
const Home = lazy(() => import('./views/Home'))
const Farms = lazy(() => import('./views/Farms'))

const Home3 = lazy(() => import('./views/Layer/Home'))
const Farms3 = lazy(() => import('./views/Layer/Farms'))

const Lottery = lazy(() => import('./views/Lottery'))
const Pools = lazy(() => import('./views/Pools'))
const Ifos = lazy(() => import('./views/Ifos'))
const NotFound = lazy(() => import('./views/NotFound'))
const Nft = lazy(() => import('./views/Nft'))
const Gaming = lazy(() => import('./views/Gaming'))
// const Layered = lazy(() => import('./views/Layered'))
const Roadmap = lazy(() => import('./views/Roadmap'))
const Whitepaper = lazy(() => import('./views/Whitepaper'))
const Profile = lazy(() => import('./views/Profile'))

// This config is required for number formating
BigNumber.config({
  EXPONENTIAL_AT: 1000,
  DECIMAL_PLACES: 80,
})

const App: React.FC = () => {
  const { account, connect } = useWallet()
  useEffect(() => {
    if (!account && window.localStorage.getItem('accountStatus')) {
      connect('injected')
    }
  }, [account, connect])

  useFetchPublicData()
  useFetchPublicData()
  useFetchProfile()
  useFetchPriceList()

  return (
    <Router>
      <ResetCSS />
      <GlobalStyle />
      <Menu>
        <Suspense fallback={<PageLoader />}>
          <Switch>
            <Route path="/" exact>
              <Home />
            </Route>
            <Route path="/farms">
              <Farms />
            </Route>
            <Route path="/pools">
              <Farms tokenMode />
            </Route>
            <Route path="/layer/info" exact>
              <Home3 />
            </Route>
            <Route path="/layer/farms">
              <Farms3 />
            </Route>
            <Route path="/layer/pools">
              <Farms3 tokenMode />
            </Route>
            <Route path="/bnbpool">
             <Pools />
            </Route>
            <Route path="/Gaming">
              <Gaming />
            </Route>
            <Route path="/Whitepaper">
              <Whitepaper />
            </Route>
            <Route path="/lottery">
              <Lottery />
            </Route>
            <Route path="/profile">
              <Profile />
            </Route>
            {/* <Route path="/Layered">
              <Layered />
            </Route> */}
            <Route path="/ifo">
             <Ifos />
            </Route>
            {/* <Route path="/nft"> */}
            {/*  <Nft /> */}
            {/* </Route> */}
            {/* Redirect */}
            {/* <Route path="/staking"> */}
            {/*  <Redirect to="/pools" /> */}
            {/* </Route> */}
            <Route path="/Nft">
              <Nft />
            </Route>
            <Route path="/Roadmap">
              <Roadmap />
            </Route>
            <Route path="/syrup">
              <Redirect to="/pools" />
            </Route>
            <Route path="/nests">
              <Redirect to="/pools" />
            </Route>
            {/* 404 */}
            <Route component={NotFound} />
          </Switch>
        </Suspense>
      </Menu>
    </Router>
  )
}

export default React.memo(App)
